import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Todo } from 'src/models/todo.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public mode: string = 'list';
  public todos: Todo[] = [];
  public title: string='Minhas tarefas';
  public form: FormGroup;

  constructor(private fb: FormBuilder) {
    this.form = this.fb.group({
      title: ['', Validators.compose([
        Validators.minLength(3),
        Validators.maxLength(60),
        Validators.required
      ])]
    });
    this.load();
    // this.todos.push(new Todo(1, 'Passear com o cachorro', false));
    // this.todos.push(new Todo(2, 'Ir ao supermercado', false));
    // this.todos.push(new Todo(3, 'Cortar o cabelo', true));
  }

  add(){
    //this.form.value => {title: 'title'}; outra forma de chamar a chamada abaixo
    const title = this.form.controls['title'].value;
    const id = this.todos.length + 1;
    this.todos.push(new Todo(id,title, false));
    this.save();//para persistir os dados session/local storage
    this.clear();
  }

  clear(){
    this.form.reset();
  }

  remove(todo: Todo){

    const index = this.todos.indexOf(todo);
    if(index !== -1){
      this.todos.splice(index, 1);
    }
    this.save();
  }

  markAsDone(todo: Todo){
    todo.done = true; 
    this.save();
  }

  markAsUndone(todo: Todo){
    todo.done = false; 
    this.save();
  }

  save(){
    const data = JSON.stringify(this.todos);
    localStorage.setItem('todos', data); //para salvar na local storage
    //sessionStorage.setItem('todos', data); // para salvar na session storage
    this.mode = 'list';
  }

  load(){
    const data = localStorage.getItem('todos');
    if(data)
    this.todos = data !== null ? JSON.parse(data) :this. todos = []; // verifica se o data vem null seta o data pra array vazio
  }

  changeMode(mode: string){
    this.mode = mode;
  }
}
<h1 class="title  animated jackInTheBox">{{title}}</h1>
<ul class="animated flipInX" *ngIf="mode == 'list'">
    <!-- <p *ngIf="todos.lenght == 0" ><small>Não tem nenhuma tarefa hoje!</small></p> -->
    <li *ngFor="let todo of todos">
        {{todo.id}}. {{todo.title}} -
        <button *ngIf="!todo.done" type="button" (click)="markAsDone(todo)"> <i class="far fa-check-circle"></i> Concluir</button> |
        <button *ngIf="todo.done" type="button" (click)="markAsUndone(todo)"> <i class="fas fa-check-circle"></i> Refazer</button> |
        <button type="button" (click)="remove(todo)"> <i class="far fa-trash-alt"></i> Remover</button>
    </li>
</ul>

<div id="modal" class="animated flipInX" *ngIf="mode == 'add'">
    <h1 class="text-center">Nova tarefa</h1>
    <form [formGroup]="form">
        <fieldset class="text-center">
            <label>Tarefa: </label>
            <input type="text" placeholder="Título da Tarefa" maxlength="60" formControlName="title">
            <!-- <small class="danger" *ngIf="form.controls.title.invalid && !form.controls.title.pristine">Esta campo deve conter entre 3 e 60 caracteres</small> -->
            <small *ngIf="form.get('title')?.errors?.['invalid']">Esta campo deve conter entre 3 e 60 caracteres</small>
            <button class="button" [disabled]="form.invalid" (click)="add()"><i class="fas fa-save"></i>Salvar</button>
        </fieldset>
    </form>
</div>

<button class="add-button animated fadeInUp button" (click)="changeMode('add')" *ngIf="mode == 'list'">
    <i class="fas fa-plus-circle"></i>
    Nova tarefa
</button>
<button class="add-button animated fadeInUp button" (click)="changeMode('list')" *ngIf="mode == 'add'">
    <i class="fas fa-times"></i>
    Cancelar
</button>

<!-- tipos de binding(data bindings)
() = HTML => TS event binding
[] = TS => HTML property binding
[()] TWO WAY BINDING
{{}}  = TS => HTML interpolation
Diretivas de componentes: usado com um modelo. Esse tipo de diretiva é a mais comum.

Ex: <app-listarPensamentos>.

Diretivas estruturais: altera o layout do DOM adicionando e removendo elementos DOM.

Ex: NgIf, NgFor. NgSwitch.

Diretivas de atributos: altera a aparência ou o comportamento de um elemento, componente ou outra diretiva.

Ex: NgClass, NgStyle
-->
